import classes from './Checkout.module.css';
import { useRef, useState } from 'react';
const Checkout = (props) =>{
    const name = useRef();
    const street = useRef();
    const zipcode = useRef();
    const city = useRef();
    const [nameIsValid, setNameIsValid] = useState(false);
    const [streetIsValid, setStreetIsValid] = useState(false);
    const [zipcodeIsValid, setZipcodeIsValid] = useState(false);
    const [cityIsValid, setCityIsValid] = useState(false);
    const [formIsValid, setFormIsValid] = useState(false);

 


    const formSubmitHandler = (event) =>{
        event.preventDefault();
        // console.log(name.current.value);
        if(name.trim().length!==0){
            setNameIsValid(true);
        }
        if(street.trim().length!==0){
            setStreetIsValid(true);
        }
        if(city.trim().length!==0){
            setCityIsValid(true);
        }
        if(zipcode.trim().length===5){
            setZipcodeIsValid(true);
        }
        if(nameIsValid&&streetIsValid&&zipcodeIsValid&&cityIsValid){
            setFormIsValid(true);
        }
        if(formIsValid){
            console.log(
                name.current.value,
                
            );

        }
    }
    return(
        <form onSubmit={formSubmitHandler}>
            <div className={classes.control}>
                <label htmlFor='name'>Your Name</label>
                <input ref = {name} type='text' id = 'name'></input>
            </div>
            <div className={classes.control}>
                <label htmlFor='street'>Street Name</label>
                <input ref = {street} type='text' id = 'street'></input>
            </div>
            <div className={classes.control}>
                <label htmlFor='zipcode'>Zipcode</label>
                <input ref = {zipcode} type='text' id = 'zipcode'></input>
            </div>
            <div className={classes.control}>
                <label htmlFor='city'>City</label>
                <input ref = {city} type='text' id = 'city'></input>
            </div>
            <button type = "button" onClick={props.onCancel}>Cancel!</button>
            <button>Confirm!</button>
        </form>
    )
}

export default Checkout;
