import { createSlice } from "@reduxjs/toolkit";


const cartslice = createSlice({
    name:"cart",
    initialState:{cartItem: 0},
    reducers:{
        addItem(state){
            state.cartItem++;
        },
        removeItem(state){
            state.cartItem--;
        }
    }
})

export const cartAddHandler = cartslice.actions;
export default cartslice;
