import { createSlice } from "@reduxjs/toolkit";

const initialCartState = {cartItem: 0}
const cartslice = createSlice({
    name:"cartState",
    state:{initialCartState},
    reducers:{
        addItem(state){
            state.cartItem++;
        }
    }
})

export const cartAddHandler = cartslice.actions;
export default cartslice.reducer;
